const createPlugin = require('@axway/api-builder-oas-flow-node')(module);
const path = require('path');

module.exports = function init(pluginConfig, options) {
	return createPlugin(pluginConfig, {
		...options,
		// we default to process.cwd for backward compatibility
		oasDir: path.join(options.appDir || process.cwd(), 'swagger'),
		configPrefix: ''
	});
};

