/**
 * API Builder components.
 * @public
 */
class Components {
	/**
	 * The swagger schema for an array of Components.
	 * @return {object} - The schema for an array of Components.
	 */
	static get schema() {
		return {
			type: 'array',
			items: {
				type: 'object',
				properties: {
					name: {
						type: 'string'
					},
					description: {
						type: 'string'
					},
					type: {
						type: 'string',
						enum: [
							'plugin',
							'connector'
						]
					},
					currentVersion: {
						type: 'string'
					},
					installed: {
						type: 'boolean'
					},
					docs: {
						type: 'string',
						format: 'uri'
					},
					package: {
						type: 'string'
					}
				},
				required: [
					'name',
					'description',
					'type',
					'builtIn',
					'installed',
					'package'
				]
			}
		};
	}
}

module.exports = Components;
