class Connector {
	static get schema() {
		return {
			title: 'Connector',
			properties: {
				name: {
					type: 'string'
				},
				description: {
					type: 'string'
				},
				version: {
					type: 'string'
				}
			},
			required: [
				'name',
				'description'
			]
		};
	}

	static validate(connector) {
		let valid = true;
		this.schema.required.forEach(prop => {
			if (!{}.hasOwnProperty.call(connector, prop)) {
				valid = false;
			}
		});
		return valid;
	}
}

module.exports = Connector;
