const { arrowQueryOptions } = require('../../util');

module.exports = ({ operationId, schemaRef, model, encodedModelName }) => {
	return {
		swagger: '2.0',
		info: {
			title: `${model.name}`,
			version: '1.0.0'
		},
		paths: {
			[`/${encodedModelName}/query`]: {
				get: {
					operationId,
					tags: [
						`${model.name}`
					],
					summary: `Query a ${model.name}`,
					consumes: [],
					produces: [
						'application/json'
					],
					parameters: [ ...arrowQueryOptions ],
					responses: {
						200: {
							description: 'The query succeeded, and the results are available.',
							schema: {
								type: 'array',
								items: {
									$ref: schemaRef.full
								}
							}
						},
						400: {
							description: 'Bad request.'
						},
						404: {
							description: 'No results were found.'
						},
						500: {
							description: 'Something went wrong during the request; check out the logs on your server.'
						}
					}
				}
			}
		}
	};
};
