const countAPIV1 = require('./countAPIV1');
const countAPIV2 = require('./countAPIV2');

const countAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return countAPIV1(name, schemaRef, model, options);
	} else {
		return countAPIV2(name, schemaRef, model, options);
	}
};

module.exports = countAPI;
