const createAPIV1 = require('./createAPIV1');
const createAPIV2 = require('./createAPIV2');

const createAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return createAPIV1(name, schemaRef, model, options);
	} else {
		return createAPIV2(name, schemaRef, model, options);
	}
};

module.exports = createAPI;
