const deleteAPIV1 = require('./deleteAPIV1');
const deleteAPIV2 = require('./deleteAPIV2');

const deleteAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return deleteAPIV1(name, schemaRef, model, options);
	} else {
		return deleteAPIV2(name, schemaRef, model, options);
	}
};

module.exports = deleteAPI;
