const deleteAllAPIV1 = require('./deleteAllAPIV1');
const deleteAllAPIV2 = require('./deleteAllAPIV2');

const deleteAllAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return deleteAllAPIV1(name, schemaRef, model, options);
	} else {
		return deleteAllAPIV2(name, schemaRef, model, options);
	}
};

module.exports = deleteAllAPI;
