const distinctAPIV1 = require('./distinctAPIV1');
const distinctAPIV2 = require('./distinctAPIV2');

const distinctAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return distinctAPIV1(name, schemaRef, model, options);
	} else {
		return distinctAPIV2(name, schemaRef, model, options);
	}
};

module.exports = distinctAPI;
