const findAllAPIV1 = require('./findAllAPIV1');
const findAllAPIV2 = require('./findAllAPIV2');

const findAllAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return findAllAPIV1(name, schemaRef, model, options);
	} else {
		return findAllAPIV2(name, schemaRef, model, options);
	}
};

module.exports = findAllAPI;
