const findAndModifyAPIV1 = require('./findAndModifyAPIV1');
const findAndModifyAPIV2 = require('./findAndModifyAPIV2');

const findAndModifyAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return findAndModifyAPIV1(name, schemaRef, model, options);
	} else {
		return findAndModifyAPIV2(name, schemaRef, model, options);
	}
};

module.exports = findAndModifyAPI;
