const findByIDAPIV1 = require('./findByIDAPIV1');
const findByIDAPIV2 = require('./findByIDAPIV2');

const findByIDAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return findByIDAPIV1(name, schemaRef, model, options);
	} else {
		return findByIDAPIV2(name, schemaRef, model, options);
	}
};

module.exports = findByIDAPI;
