const queryAPIV1 = require('./queryAPIV1');
const queryAPIV2 = require('./queryAPIV2');

const queryAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return queryAPIV1(name, schemaRef, model, options);
	} else {
		return queryAPIV2(name, schemaRef, model, options);
	}
};

module.exports = queryAPI;
