const updateAPIV1 = require('./updateAPIV1');
const updateAPIV2 = require('./updateAPIV2');

const updateAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return updateAPIV1(name, schemaRef, model, options);
	} else {
		return updateAPIV2(name, schemaRef, model, options);
	}
};

module.exports = updateAPI;
