const upsertAPIV1 = require('./upsertAPIV1');
const upsertAPIV2 = require('./upsertAPIV2');

const upsertAPI = (name, schemaRef, model, options) => {
	if (!options.errorOutputs) {
		return upsertAPIV1(name, schemaRef, model, options);
	} else {
		return upsertAPIV2(name, schemaRef, model, options);
	}
};

module.exports = upsertAPI;
