
/**
 * The controller implementing the actions for interacting with Triggers.
 * @public
 */
class TriggersController {
	constructor(ctx, controllers) {
		this.ctx = ctx;
		this.controllers = controllers;
	}

	getDefinitions() {
		return this.ctx.apibuilder._internal
			.getTriggerManager().getInfo().definitions;
	}
}

module.exports = TriggersController;
